package service;

import models.Cart;
import models.Description;
import models.Product;

import java.math.RoundingMode;
import java.text.DecimalFormat;

import java.util.ArrayList;
import java.util.List;

public class ProductService {

	public static List<Cart> cart = new ArrayList<Cart>();

	public static String locale;
	
	public static double totalPrice; 
	
	private static DecimalFormat df2 = new DecimalFormat("#.##");

	public static List<Product> getProducts() {

		List<Product> productList = new ArrayList<Product>();

		// Product Data

		List<Description> descriptionListOne = new ArrayList<Description>();

		Description d1 = new Description(1, "no_NO", "Kjempe bra headset med god lyd!");
		Description d11 = new Description(1, "en_US", "Very good headset with great sound!");

		descriptionListOne.add(d1);
		descriptionListOne.add(d11);

		List<Description> descriptionListTwo = new ArrayList<Description>();

		Description d2 = new Description(2, "no_NO", "Frisk øl med god fylde og aroma");
		Description d22 = new Description(2, "en_US", "Fresh beer with good depth and aroma");

		descriptionListTwo.add(d2);
		descriptionListTwo.add(d22);

		List<Description> descriptionListThree = new ArrayList<Description>();

		Description d3 = new Description(3, "no_NO", "Sause-panne med 10-års garanti, diameter: 35 cm");
		Description d33 = new Description(3, "en_US", "Sauce-pan with 10 years warranty, diameter: 35 cm");

		descriptionListThree.add(d3);
		descriptionListThree.add(d33);

		Product p1 = new Product(1, "Sony-Headset", 150.0,
				"https://www.sony.no/image/134a2eefe22c3000f28cf57296b6bcb5?fmt=png-alpha&wid=240", descriptionListOne);

		Product p2 = new Product(2, "Beer", 2.3,
				"data:image/jpeg;base64,",
				descriptionListTwo);

		Product p3 = new Product(3, "Sauce-Pan", 39.90,
				"data:image/jpeg;base64,",
				descriptionListThree);

		productList.add(p1);
		productList.add(p2);
		productList.add(p3);
		return productList;

	}

	public static void addToCart(int pno) {
		List<Product> products = getProducts();

		for (Product product : products) {
			if (product.getPno() == pno) {
				if (exists(cart, pno)) {
					return;
				}

				Cart c = new Cart(product.getPno(), product.getpName(), product.getPriceInEuro(),
						product.getDescription());
				cart.add(c);
				totalPrice += c.getPriceInEuro(); 
				c.setTotalPrice(c.getPriceInEuro());
				return;
			}
		}

	}

	private static boolean exists(List<Cart> cartItems, int pno) {

		for (Cart c : cartItems) {
			if (c.getPno() == pno) {
				c.updateCount();
				totalPrice += c.getPriceInEuro(); 
				c.setTotalPrice(c.getPriceInEuro());
				return true;
			}
		}
		return false;
	}

	public static List<Cart> getCart() {
		return cart;
	}

	public static void setLocale(String locale) {
		ProductService.locale = locale;
	}

	public static String getLocale() {
		return ProductService.locale;
	}
	
	public static String getTotalPrice() {
		return df2.format(totalPrice); 
	}

}
